package basics;

public class s10operators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int res=1+2;
		System.out.println("Result : "+res);
		res++;
		System.out.println(res);
		res+=2;
		System.out.println("+ :"+res);
		res*=2;
		System.out.println("* :"+res);
		res-=10;
		System.out.println("- :"+res);
	}

}